INSERT INTO `wp_posts` VALUES(438, 2, '2010-10-18 20:43:13', '2010-10-18 20:43:13', '<div class="map floatLeft">\r\n<iframe width="535" height="370" frameborder="0" scrolling="no" marginheight="0" marginwidth="0" src="http://maps.google.com/maps?f=q&amp;source=s_q&amp;hl=en&amp;geocode=&amp;q=12th+%26+Porter,+Nashville,+TN&amp;sll=37.0625,-95.677068&amp;sspn=46.14027,135.263672&amp;ie=UTF8&amp;hq=12th+%26+Porter,&amp;hnear=Nashville,+Davidson,+Tennessee&amp;ll=36.157862,-86.788136&amp;spn=0.017661,0.008124&amp;output=embed"></iframe>\r\n</div>\r\n<div class="directiontext floatRight">\r\n<h3 class="biznae"><span style="color: #f26d7d;">12th&amp;Porter</span></h3>\r\n<strong>114 12th Avenue N.\r\nNashville, TN  37203\r\nph# (615) 320-3745</strong>\r\n<p>12th & Porter is located in the heart of downtown Nashville. From highways I-65 or I-40 coming into Nashville, take the Broadway Exit and head East towards downtown. At the intersection of 12th Avenue and Broadway take a left onto 12th Avenue North. Landmarks at the intersection include an Exxon gas station and a McDonald’s. 12th & Porter is the blue building one block down on the right. Please park in the paved lot across the street or the gravel lot at the end of the road. 114 12th Avenue North, Nashville TN 37203.</p>\r\n</div>\r\n<div class="clear"></div>', 'Directions', '', 'inherit', 'open', 'open', '', '168-revision-5', '', '', '2010-10-18 20:43:13', '2010-10-18 20:43:13', '', 168, 'http://12thandporter.dev/2010/uncategorized/168-revision-5/', 0, 'revision', '', 0);
